.tab-bar {
  height: 29px;
  background: #000000;
  padding: 0 0px 0 5px;
  overflow-x: auto;
  overflow-y: hidden;
}
.tab-bar::-webkit-scrollbar {
  display: none;
}
.tab-bar:after {
  content: "";
  position: absolute;
  bottom: -1px;
  height: 1px;
  left: 0;
  right: 0;
  background-color: #000500;
  border-top: 1px solid #005500;
  border-bottom: 1px solid #003300;
  pointer-events: none;
}
.tab-bar .tab {
  position: relative;
  top: 2px;
  max-width: 150px;
  height: 26px;
  line-height: 26px;
  padding: 0 5px 0 8px;
  margin: 0;
  color: #009900;
  letter-spacing: -0.1px;
  font-size: 12px;
  transition: color 0.1s ease-in;
  border: none;
}
.tab-bar .tab,
.tab-bar .tab:before,
.tab-bar .tab:after {
  background: transparent;
  height: 26px;
}
.tab-bar .tab:before,
.tab-bar .tab:after {
  content: '';
  position: absolute;
  top: 0px;
  width: 0px;
  height: 26px;
}
.tab-bar .tab:before {
  left: 0px;
  border-top-left-radius: 1px;
  -webkit-transform: skewX(0deg);
}
.tab-bar .tab:after {
  right: 0px;
  border-top-right-radius: 1px;
  -webkit-transform: skewX(0deg);
}
.tab-bar .tab .close-icon {
  right: 8px;
  z-index: 3;
  text-align: right;
  line-height: 26px;
  color: #00cc00;
}
.tab-bar .tab .close-icon:hover {
  color: inherit;
}
.tab-bar .tab.modified:not(:hover) .close-icon {
  right: 8px;
  border: 2px solid #94ff95;
  top: 10px;
  width: 8px;
  height: 8px;
  box-shadow: 0px 0px 10px #64ff64;
}
.tab-bar .tab.modified:hover .close-icon:hover {
  color: #aaffaa;
}
.tab-bar .tab .title {
  position: relative;
  z-index: 1;
  padding-right: 10px;
}
.tab-bar .tab.active {
  z-index: 1;
  color: #aaffaa;
  text-shadow: 0px 0px 5px rgba(100, 255, 100, 0.5);
  box-shadow: inset 0px 0px 10px 5px black;
  background: repeating-linear-gradient(0deg, #000000 1px, #000000 1px, #006600 3px, #006600 1px);
}
.tab-bar .tab.active .title::before {
  text-shadow: none;
}
.tab-bar .tab.active .close-icon {
  line-height: 25px;
  color: #00cc00;
  right: 8px;
}
.tab-bar .tab.active,
.tab-bar .tab.active:before,
.tab-bar .tab.active:after {
  height: 27px;
  border-top: 1px solid #1e7a3d;
}
.tab-bar .tab.active:before {
  border-left: 1px solid #1e7a3d;
  margin-top: -1px;
}
.tab-bar .tab.active:after {
  border-right: 1px solid #1e7a3d;
  margin-top: -1px;
}
.tab-bar .tab:hover {
  color: #aaffaa;
  text-shadow: 0px 0px 10px green;
}
.tab-bar .tab.active:hover .close-icon {
  color: #00cc00;
}
.tab-bar .tab.active:hover .close-icon:hover {
  color: inherit;
}
.tab-bar .placeholder {
  height: 29px;
  margin-left: -9px;
  pointer-events: none;
}
.tab-bar .placeholder:after {
  top: 27px;
}
