@-webkit-keyframes neon1 {
  from {
    text-shadow: 0 0 0px #ffffff, 0 0 3px #ffffff, 0 0 6.6px #ff1177, 0 0 16.9px #ff1177;
  }
  to {
    text-shadow: 0 0 0.6px #ffffff, 0 0 3.7px #ffffff, 0 0 7.3px #ff1177, 0 0 17.3px #ff1177;
  }
}
@-webkit-keyframes neon2 {
  from {
    text-shadow: 0 0 10px #fff,
             0 0 20px  #fff,
             0 0 30px  #fff,
             0 0 40px  #228DFF,
             0 0 70px  #228DFF,
             0 0 80px  #228DFF,
             0 0 100px #228DFF,
             0 0 150px #228DFF;
  }
  to {
    text-shadow: 0 0 5px #fff,
             0 0 10px #fff,
             0 0 15px #fff,
             0 0 20px #228DFF,
             0 0 35px #228DFF,
             0 0 40px #228DFF,
             0 0 50px #228DFF,
             0 0 75px #228DFF;
  }
}
@-webkit-keyframes neon3 {
  from {
    text-shadow: 0 0 10px #fff,
             0 0 20px  #fff,
             0 0 30px  #fff,
             0 0 40px  #FFDD1B,
             0 0 70px  #FFDD1B,
             0 0 80px  #FFDD1B,
             0 0 100px #FFDD1B,
             0 0 150px #FFDD1B;
  }
  to {
    text-shadow: 0 0 5px #fff,
             0 0 10px #fff,
             0 0 15px #fff,
             0 0 20px #FFDD1B,
             0 0 35px #FFDD1B,
             0 0 40px #FFDD1B,
             0 0 50px #FFDD1B,
             0 0 75px #FFDD1B;
  }
}
@-webkit-keyframes neon4 {
  from {
    text-shadow: 0 0 10px #fff,
             0 0 20px  #fff,
             0 0 30px  #fff,
             0 0 40px  #B6FF00,
             0 0 70px  #B6FF00,
             0 0 80px  #B6FF00,
             0 0 100px #B6FF00,
             0 0 150px #B6FF00;
  }
  to {
    text-shadow: 0 0 5px #fff,
             0 0 10px #fff,
             0 0 15px #fff,
             0 0 20px #B6FF00,
             0 0 35px #B6FF00,
             0 0 40px #B6FF00,
             0 0 50px #B6FF00,
             0 0 75px #B6FF00;
  }
}
@-webkit-keyframes neon5 {
  from {
    text-shadow: 0 0 10px #fff,
             0 0 20px  #fff,
             0 0 30px  #fff,
             0 0 40px  #FF9900,
             0 0 70px  #FF9900,
             0 0 80px  #FF9900,
             0 0 100px #FF9900,
             0 0 150px #FF9900;
  }
  to {
    text-shadow: 0 0 5px #fff,
             0 0 10px #fff,
             0 0 15px #fff,
             0 0 20px #FF9900,
             0 0 35px #FF9900,
             0 0 40px #FF9900,
             0 0 50px #FF9900,
             0 0 75px #FF9900;
  }
}
@-webkit-keyframes box1 {
  from {
    box-shadow: 0px 0px 1px 0px #ffffff, 0px 0px 4px 0.5px #ffffff, 0px 0px 4.5px 0.9px #ffffff, 0px 0px 5px 3px #d09011, 0px 0px 7px 5px #d09011, 0px 0px 35px 0px #d09011;
  }
  to {
    box-shadow: 0px 0px 3.3px 0px #ffffff, 0px 0px 6.3px 0.5px #ffffff, 0px 0px 6.8px 1.6px #ffffff, 0px 0px 7.3px 3.7px #d09011, 0px 0px 9.3px 5.7px #d09011, 0px 0px 37.3px 2px #d09011;
  }
}
@-webkit-keyframes neon6 {
  from {
    text-shadow: 0 0 0px #ffffff, 0 0 10px #ffffff, 0 0 10px #ffffff, 0 0 0px #ff00de, 0 0 7px #ff00de, 0 0 15px #ff00de;
  }
  to {
    text-shadow: 0 0 9px #ffffff, 0 0 25px #ffffff, 0 0 35px #ffffff, 0 0 20px #ff00de, 0 0 35px #ff00de, 0 0 80px #ff00de;
  }
}
/*=============================================================================
=    Subtle Animations for source text
=============================================================================*/
@-webkit-keyframes text-pink {
  from {
    text-shadow: 0 0 0px #ffffff, 0 0 10px #ffffff, 0 0 10px #ffffff, 0 0 0px #ff00de, 0 0 7px #ff00de, 0 0 15px #ff00de;
  }
