atom-text-editor,
:host {
  background-color: #0e0b13;
  color: #e5e3e8;
  font-family: 'Source Code Pro';
  font-weight: normal;
  -webkit-font-smoothing: antialiased !important;
  -webkit-text-stroke-width: 0.03em;
  text-shadow: none;
}
atom-pane:not(.active) atom-text-editor {
  color: rgba(255, 255, 255, 0.6);
}
atom-pane:not(.active) atom-text-editor .underlayer {
  background-color: #0f0e10;
}
atom-pane-container atom-pane {
  background-color: #212426;
}
atom-pane-container atom-pane:not(.active) /deep/ .source {
  -webkit-filter: brightness(60%) opacity(0.8);
}
atom-pane-container atom-pane:not(.active) /deep/ .gutter {
  opacity: 0.7;
}
atom-pane-container atom-pane:not(.active) /deep/ .gutter .line-number {
  opacity: 0.5 !important;
}
atom-pane-container atom-pane:not(.active) /deep/ .line {
  background-color: transparent;
}
atom-pane-container atom-pane:before {
  background-color: rgba(34, 25, 51, 0.9);
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  content: ' ';
}
atom-text-editor .gutter,
:host .gutter {
  background-color: #0c0a10;
  color: #454148;
}
atom-text-editor .gutter > .line-numbers:first-child,
:host .gutter > .line-numbers:first-child {
  background-color: #150e20;
  box-shadow: inset 1px 0px #1b1726, inset -1px 0px #000000;
}
atom-text-editor::shadow atom-text-editor-minimap,
atom-text-editor atom-text-editor-minimap {
  background: rgba(22, 20, 26, 0.4);
  box-shadow: inset 0px 17px 56px rgba(0, 0, 0, 0.4);
}
atom-pane:not(.active) atom-text-editor::shadow atom-text-editor-minimap,
atom-text-editor atom-text-editor-minimap {
  background: rgba(0, 0, 0, 0.8);
}
atom-text-editor::shadow atom-text-editor-minimap::shadow .minimap-visible-area,
atom-text-editor atom-text-editor-minimap::shadow .minimap-visible-area {
  background-color: #878787;
  opacity: .2;
  background: linear-gradient(90deg, #3e3d43 0%, #c6c3d5 50%, #3e3d43 100%);
  box-shadow: inset 0px 17px 38px rgba(0, 0, 0, 0.4);
}
atom-text-editor .gutter .line-number.cursor-line,
:host .gutter .line-number.cursor-line {
  background-color: #14121c;
  color: rgba(157, 152, 179, 0.65);
  text-shadow: 0px -1px rgba(0, 0, 0, 0.2), 0px 1px rgba(255, 255, 255, 0.1);
}
atom-text-editor .gutter .line-number.cursor-line-no-selection,
:host .gutter .line-number.cursor-line-no-selection {
  color: rgba(157, 152, 179, 0.65);
}
atom-text-editor .wrap-guide,
:host .wrap-guide {
  color: #0d0a15;
  background-color: #080708;
  border-right: none;
  border-left: 1px solid #352b46;
  box-shadow: none;
  z-index: 0;
}
atom-text-editor .invisible-character,
:host .invisible-character {
  color: rgba(43, 42, 45, 0.85);
}
atom-text-editor .indent-guide,
:host .indent-guide {
  color: #2b2a2d;
  background: none;
  box-shadow: inset 1px 0 #2b2438;
}
atom-text-editor .indent-guide:first-child,
:host .indent-guide:first-child {
  box-shadow: none;
}
atom-text-editor.is-focused .cursor,
:host(.is-focused) .cursor {
  border-color: #ff1018;
}
:host(.is-focused) .selection .region {
  background-color: rgba(123, 90, 140, 0.5);
}
atom-text-editor.is-focused .line-number.cursor-line-no-selection,
atom-text-editor.is-focused .line.cursor-line,
:host(.is-focused) .line-number.cursor-line-no-selection,
:host(.is-focused) .line.cursor-line {
  background-color: #1b1523;
  border: none;
  z-index: 101;
}
atom-text-editor.is-focused .line-number.cursor-line-no-selection .entity.name.function,
atom-text-editor.is-focused .line.cursor-line .entity.name.function,
:host(.is-focused) .line-number.cursor-line-no-selection .entity.name.function,
:host(.is-focused) .line.cursor-line .entity.name.function {
  -webkit-transition: all 1s;
  -webkit-animation: neon6 1s ease-in-out infinite alternate;
}
/**
 * Find Results
 */
atom-text-editor .search-results .marker .region,
:host .search-results .marker .region {
  background-color: transparent;
  border: #d09011;
  -webkit-transition: all 0.5s;
  -webkit-animation: neon3 0.5s ease-in-out infinite alternate;
}
atom-text-editor .highlight .current-result .region,
:host .current-result .region,
atom-text-editor::shadow .current-result .region {
  border: #f29a7d;
  border-radius: 7px;
  border: 2px solid rgba(156, 108, 13, 0.8);
  box-sizing: border-box;
  -webkit-transition: all 0.5s;
  -webkit-animation: box1 0.5s ease-in-out infinite alternate;
  border-style: solid;
  box-sizing: content-box;
  transform: translate(-1px, -1px);
  box-shadow: 0px 0px 22px 0px #d09011;
  background: rgba(151, 151, 149, 0.1);
}
atom-text-editor .find-result .region,
atom-text-editor::shadow .find-result .region {
  border-radius: 3px;
  border: 1px solid #d09011;
  box-sizing: border-box;
  -webkit-transition: all 0.5s;
  -webkit-animation: neon1 0.5s ease-in-out infinite alternate;
  border-style: solid;
}
/*=========================================================================
=    SYNTAX STYLES
=========================================================================*/
.source {
  color: #d0cfd3;
  -webkit-text-stroke-width: 0.05em;
  -webkit-filter: saturate(100%) brightness(90%) contrast(90%);
}
.comment {
  color: #5c5c70;
  font-weight: normal;
  -webkit-text-stroke-width: 0.04em;
}
.comment .block.documentation {
  color: #636379;
}
.string {
  color: #ecdd93;
}
.brace + .string,
.begin ~ .string:not(:last-child) {
  color: #ecdd93;
}
.constant.numeric {
  color: #00a9c7;
}
.meta.period + .constant.numeric,
.constant.numeric + .meta.period {
  color: #00a9c7;
}
.constant.language {
  color: #c9f075;
}
.constant.character,
.constant.other {
  color: #faa629;
}
.punctuation.definition.parameters {
  font-style: italic;
  font-weight: normal;
  color: #a3c1c7;
}
.variable {
  color: #faa629;
  font-weight: bold;
  font-style: italic;
}
.keyword {
  color: #c03571;
  font-weight: bold;
  -webkit-text-stroke: 0.02em;
}
.keyword.operator {
  color: #faa629;
}
.storage {
  color: #ff5d38;
  font-weight: bold;
  font-style: italic;
}
.storage.type {
  font-weight: bold;
  color: #7ecdf1;
  font-style: normal;
}
.comment.block .storage.type {
  color: #2991a3;
}
.entity {
  color: #faa629;
}
.entity.name.class {
  text-decoration: underline;
  color: #ff5d38;
  font-weight: 700;
}
.entity.other.inherited-class {
  font-style: italic;
  text-decoration: underline;
  color: #ff5d38;
}
.entity.name.function {
  font-weight: 900;
  color: #a3c1c7;
}
.entity.name.tag {
  color: #dedede;
}
.entity.other.attribute-name {
  color: #ff7859;
}
.variable.parameter {
  font-style: italic;
  color: #ffa016;
}
.support.function {
  color: #7bb7b0;
}
.support.constant {
  color: #5debfd;
}
.support.type {
  color: #4b6672;
  font-weight: bold;
}
.support.class {
  color: #4b6672;
  font-weight: bold;
}
.invalid {
  color: #f8f8f0;
  background-color: #00a8c6;
}
.invalid.deprecated {
  color: #f8f8f0;
  background-color: #00a8c6;
}
.meta.structure.dictionary.json .string.quoted.double.json {
  color: #bcd42a;
}
.meta.diff,
.meta.diff.header {
  color: #75715E;
}
.markup.deleted {
  color: #00A8C6;
}
.markup.inserted {
  color: #A6E22E;
}
.markup.changed {
  color: #E6DB74;
}
.constant.numeric.line-number.find-in-files:not(.match) {
  color: rgba(143, 190, 0, 0.63);
}
.entity.name.filename.find-in-files {
  color: #E6DB74;
}
.keyword.other {
  color: #b7b1a9;
}
.meta.property-value,
.support.constant.property-value,
.constant.other.color {
  color: #eeffe0;
}
.meta.structure.dictionary.json .string.quoted.double.json {
  color: #ff5d38;
}
.meta.structure.dictionary.value.json .string.quoted.double.json {
  color: #bcd42a;
}
.meta.property-value .punctuation.separator.key-value {
  color: #ede0ce;
}
.keyword.other.use,
.keyword.other.function.use,
.keyword.other.namespace,
.keyword.other.new,
.keyword.other.special-method,
.keyword.other.unit,
.keyword.other.use-as {
  color: #26a6a6;
}
.meta.use .support.class.builtin,
.meta.other.inherited-class .support.class.builtin {
  color: #ede0ce;
}
.markup.deleted.git_gutter {
  color: #e61f44;
}
.markup.inserted.git_gutter {
  color: #a7da1e;
}
.markup.changed.git_gutter {
  color: #f7b83d;
}
.string.interpolated {
  color: #45ffcd;
}
.string.regexp {
  font-style: italic;
  color: #eb9949;
}
