@-webkit-keyframes neon1 {
  from {
    text-shadow: 0 0 0px #ffffff, 0 0 3px #ffffff, 0 0 6.6px #ff1177, 0 0 16.9px #ff1177;
  }
  to {
    text-shadow: 0 0 0.6px #ffffff, 0 0 3.7px #ffffff, 0 0 7.3px #ff1177, 0 0 17.3px #ff1177;
  }
}
@-webkit-keyframes neon2 {
  from {
    text-shadow: 0 0 10px #fff,
             0 0 20px  #fff,
             0 0 30px  #fff,
             0 0 40px  #228DFF,
             0 0 70px  #228DFF,
             0 0 80px  #228DFF,
             0 0 100px #228DFF,
             0 0 150px #228DFF;
  }
  to {
    text-shadow: 0 0 5px #fff,
             0 0 10px #fff,
             0 0 15px #fff,
             0 0 20px #228DFF,
             0 0 35px #228DFF,
             0 0 40px #228DFF,
             0 0 50px #228DFF,
             0 0 75px #228DFF;
  }
}
