/*=============================================================================
=    Subtle Animations for source text
=============================================================================*/
@-webkit-keyframes text-pink {
  from {
    text-shadow: 0 0 0px #ffffff, 0 0 10px #ffffff, 0 0 10px #ffffff, 0 0 0px #ff00de, 0 0 7px #ff00de, 0 0 15px #ff00de;
  }
  to {
    text-shadow: 0 0 5px #ffffff, 0 0 17px #ffffff, 0 0 25px #ffffff, 0 0 10px #ff00de, 0 0 20px #ff00de, 0 0 30px #ff00de;
  }
}
@-webkit-keyframes text-blue {
  from {
    text-shadow: 0 0 10px #fff,
             0 0 20px  #fff,
             0 0 20px  #fff,
             0 0 30px  #228DFF,
             0 0 50px  #228DFF,
             0 0 70px  #228DFF,
             0 0 100px #228DFF;
  }
  to {
    text-shadow: 0 0 5px #fff,
             0 0 10px #fff,
             0 0 10px #fff,
             0 0 10px #228DFF,
             0 0 25px #228DFF,
             0 0 30px #228DFF,
             0 0 45px #228DFF;
  }
}
@-webkit-keyframes text-orange {
  from {
    text-shadow: 0px 0px 1px 0px #ffffff, 0px 0px 4px 0.5px #ffffff, 0px 0px 4.5px 0.9px #ffffff, 0px 0px 5px 3px #d09011, 0px 0px 7px 5px #d09011, 0px 0px 35px 0px #d09011;
