.key-binding-resolver {
  -webkit-flex: 0.25;
  overflow: auto;
}
.key-binding-resolver .keystroke {
  margin-right: 1em;
}
.key-binding-resolver table .used {
  color: #73c990;
}
.key-binding-resolver table .unused {
  color: #a499b8;
}
.key-binding-resolver table .unmatched {
  color: rgba(164, 153, 184, 0.6);
}
.key-binding-resolver table .used .command::before,
.key-binding-resolver table .unused .command::before {
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  font-size: 16px;
  width: 16px;
  height: 16px;
  content: "\f03a";
}
.key-binding-resolver table .unmatched .command::before {
  font-family: 'Octicons Regular';
  font-weight: normal;
  font-style: normal;
  display: inline-block;
  line-height: 1;
  -webkit-font-smoothing: antialiased;
  text-decoration: none;
  font-size: 16px;
  width: 16px;
  height: 16px;
  content: "\f081";
}
.key-binding-resolver table .command,
.key-binding-resolver table .selector,
.key-binding-resolver table .source {
  min-width: 10em;
}
.key-binding-resolver table .source {
  cursor: pointer;
}
