  }
}
atom-overlay.autocomplete-plus {
  z-index: 5;
}
autocomplete-suggestion-list.select-list.popover-list {
  width: auto;
  display: inline-block;
  min-width: 200px;
  max-width: 800px;
  padding: 0;
  overflow: hidden;
}
autocomplete-suggestion-list.select-list.popover-list .suggestion-list-scroller {
  overflow-y: auto;
}
autocomplete-suggestion-list.select-list.popover-list .suggestion-description {
  padding: 5px 0;
  padding-left: 0.6em;
  padding-right: 0.6em;
  min-height: 2em;
  line-height: 1.3;
  background: #181220;
  border-radius: 0 0 3px 3px;
}
autocomplete-suggestion-list.select-list.popover-list .suggestion-description-content {
  font-size: 13px;
  font-family: '.SFNSText-Regular', 'SF UI Text', 'Lucida Grande', 'Segoe UI', Ubuntu, Cantarell, sans-serif;
}
autocomplete-suggestion-list.select-list.popover-list .suggestion-description-more-link {
  font-size: 13px;
  font-family: '.SFNSText-Regular', 'SF UI Text', 'Lucida Grande', 'Segoe UI', Ubuntu, Cantarell, sans-serif;
  color: #6494ed;
}
autocomplete-suggestion-list.select-list.popover-list input {
  position: absolute;
  opacity: 0.00;
}
autocomplete-suggestion-list.select-list.popover-list ol.list-group {
  margin-top: 0;
  display: table;
  width: 100%;
}
autocomplete-suggestion-list.select-list.popover-list ol.list-group li {
  display: table-row;
  color: #a499b8;
  line-height: 2em;
}
autocomplete-suggestion-list.select-list.popover-list ol.list-group li > span {
  display: table-cell;
  vertical-align: middle;
}
autocomplete-suggestion-list.select-list.popover-list ol.list-group li:after {
  clear: both;
  display: block;
  content: ' ';
}
autocomplete-suggestion-list.select-list.popover-list ol.list-group li.selected .word {
  color: rgba(255, 255, 255, 0.8);
}
autocomplete-suggestion-list.select-list.popover-list ol.list-group li.selected .character-match {
  color: #ffffff;
}
autocomplete-suggestion-list.select-list.popover-list ol.list-group li.selected .left-label,
autocomplete-suggestion-list.select-list.popover-list ol.list-group li.selected .right-label,
autocomplete-suggestion-list.select-list.popover-list ol.list-group li.selected .snippet-completion {
  color: rgba(255, 255, 255, 0.5);
}
autocomplete-suggestion-list .icon-container {
  width: 2em;
  padding-left: 0;
  padding-right: 0.75em;
}
autocomplete-suggestion-list .icon-container:empty {
  width: 0.6em;
  padding: 0;
}
autocomplete-suggestion-list .icon {
  display: block;
  width: 2em;
  line-height: 2em;
  text-align: center;
  font-style: normal;
  font-weight: bold;
  color: rgba(164, 153, 184, 0.6);
  background: rgba(164, 153, 184, 0.1);
}
autocomplete-suggestion-list .icon:empty {
  display: none;
}
autocomplete-suggestion-list .icon > *::before {
  font-size: 1em;
  width: 1em;
  height: 1em;
}
autocomplete-suggestion-list .icon .icon-letter {
  position: relative;
  top: -0.1em;
  font-size: 0.9em;
  line-height: 0.9em;
}
autocomplete-suggestion-list .left-label {
  text-align: right;
  padding-right: 0.75em;
  vertical-align: middle;
  font-size: 0.9em;
  color: rgba(164, 153, 184, 0.6);
}
autocomplete-suggestion-list .left-label:empty {
  padding: 0;
}
autocomplete-suggestion-list .right-label {
  float: right;
  padding-left: 0.75em;
  padding-right: 0.6em;
  font-size: 0.9em;
  color: rgba(164, 153, 184, 0.6);
}
autocomplete-suggestion-list .right-label:empty {
  width: 0.6em;
  padding: 0;
}
autocomplete-suggestion-list .word {
  float: left;
}
autocomplete-suggestion-list .character-match {
  font-weight: bold;
  color: #dad5e2;
}
autocomplete-suggestion-list .snippet-completion {
  color: rgba(164, 153, 184, 0.6);
}
autocomplete-suggestion-list .word,
autocomplete-suggestion-list .left-label,
autocomplete-suggestion-list .right-label {
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}
autocomplete-suggestion-list .left-label {
  max-width: 150px;
}
autocomplete-suggestion-list .right-label {
  max-width: 150px;
}
autocomplete-suggestion-list .word {
  max-width: 500px;
}
